import React, { useState, useRef, useEffect } from 'react';
import './ChatInterface.css';

// Info icon component
const InfoIcon = ({ onClick, isExpanded }) => (
  <button
    onClick={onClick}
    className={`ml-2 p-1 rounded-full hover:bg-gray-100 transition-colors ${
      isExpanded ? 'bg-gray-100' : ''
    }`}
    title="Show SQL query and raw data"
  >
    <svg
      width="16"
      height="16"
      viewBox="0 0 24 24"
      fill="none"
      className="text-gray-500 hover:text-gray-700"
    >
      <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2" />
      <path d="M9,9h0a3,3,0,0,1,6,0c0,2-3,3-3,3" stroke="currentColor" strokeWidth="2" />
      <path d="M12,17h0" stroke="currentColor" strokeWidth="2" />
    </svg>
  </button>
);

// Confirmation component
const ConfirmationSection = ({ message, onConfirm, isConfirming }) => {
  return (
    <div className="mt-3 p-3 bg-blue-50 rounded-lg border border-blue-200">
      <div className="flex items-center gap-2 mb-3">
        <span className="text-lg">‚ùì</span>
        <span className="text-sm font-medium text-blue-800">Please confirm:</span>
      </div>

      <div className="flex gap-2">
        <button
          onClick={() => onConfirm(true)}
          disabled={isConfirming}
          className="px-4 py-2 bg-green-600 text-white text-sm rounded hover:bg-green-700 disabled:opacity-50 transition-colors"
        >
          {isConfirming ? 'Processing...' : 'Yes, correct'}
        </button>
        <button
          onClick={() => onConfirm(false)}
          disabled={isConfirming}
          className="px-4 py-2 bg-red-600 text-white text-sm rounded hover:bg-red-700 disabled:opacity-50 transition-colors"
        >
          No, let me rephrase
        </button>
      </div>
    </div>
  );
};

// Refinement component
const RefinementSection = ({ message, onRefine, isRefining }) => {
  const [showRefineInput, setShowRefineInput] = useState(false);
  const [feedback, setFeedback] = useState('');

  const handleRefine = () => {
    if (feedback.trim()) {
      onRefine(message.originalQuestion || message.text, feedback);
      setFeedback('');
      setShowRefineInput(false);
    }
  };

  return (
    <div className="mt-3 p-3 bg-blue-50 rounded-lg border border-blue-200">
      <div className="flex items-center gap-2 mb-2">
        <span className="text-lg">üí°</span>
        <span className="text-sm font-medium text-blue-800">Refine the question?</span>
      </div>

      {!showRefineInput ? (
        <div className="flex gap-2">
          <button
            onClick={() => setShowRefineInput(true)}
            className="px-3 py-1 bg-blue-600 text-white text-sm rounded hover:bg-blue-700 transition-colors"
          >
            Yes
          </button>
          <button
            onClick={() => {/* Hide refinement section */}}
            className="px-3 py-1 bg-gray-300 text-gray-700 text-sm rounded hover:bg-gray-400 transition-colors"
          >
            No
          </button>
        </div>
      ) : (
        <div className="space-y-2">
          <textarea
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
            placeholder="How would you like me to refine or reframe the question?"
            className="w-full p-2 text-sm border border-gray-300 rounded resize-none"
            rows="2"
          />
          <div className="flex gap-2">
            <button
              onClick={handleRefine}
              disabled={!feedback.trim() || isRefining}
              className="px-3 py-1 bg-blue-600 text-white text-sm rounded hover:bg-blue-700 disabled:opacity-50 transition-colors"
            >
              {isRefining ? 'Refining...' : 'Refine'}
            </button>
            <button
              onClick={() => setShowRefineInput(false)}
              className="px-3 py-1 bg-gray-300 text-gray-700 text-sm rounded hover:bg-gray-400 transition-colors"
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

// Expandable details component
const MessageDetails = ({ sqlQuery, rawData, rowCount, isExpanded }) => {
  if (!isExpanded) return null;

  return (
    <div className="mt-3 p-3 bg-gray-50 rounded-lg border border-gray-200">
      <div className="space-y-3">
        {/* SQL Query Section */}
        <div>
          <h4 className="text-sm font-semibold text-gray-700 mb-2">SQL Query:</h4>
          <div className="bg-gray-800 text-green-400 p-3 rounded text-sm font-mono overflow-x-auto">
            {sqlQuery}
          </div>
        </div>

        {/* Raw Data Section */}
        <div>
          <h4 className="text-sm font-semibold text-gray-700 mb-2">
            Raw Data ({rowCount} rows):
          </h4>
          <div className="bg-white border rounded max-h-64 overflow-auto">
            {rawData && rawData.length > 0 ? (
              <div className="overflow-x-auto">
                <table className="min-w-full text-sm">
                  <thead className="bg-gray-50">
                    <tr>
                      {Object.keys(rawData[0]).map((key) => (
                        <th key={key} className="px-3 py-2 text-left font-medium text-gray-700 border-b">
                          {key}
                        </th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    {rawData.slice(0, 10).map((row, index) => (
                      <tr key={index} className="hover:bg-gray-50">
                        {Object.values(row).map((value, cellIndex) => (
                          <td key={cellIndex} className="px-3 py-2 border-b text-gray-600">
                            {value !== null && value !== undefined ? String(value) : 'NULL'}
                          </td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
                {rawData.length > 10 && (
                  <div className="p-2 text-center text-gray-500 text-sm border-t">
                    ... and {rawData.length - 10} more rows
                  </div>
                )}
              </div>
            ) : (
              <div className="p-4 text-center text-gray-500">No data returned</div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

const ChatInterface = ({ messages, onSendMessage, onConfirmQuestion, onRefineMessage, isLoading }) => {
  const [inputValue, setInputValue] = useState('');
  const [expandedMessages, setExpandedMessages] = useState(new Set());
  const [isRefining, setIsRefining] = useState(false);
  const [isConfirming, setIsConfirming] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputValue.trim() && !isLoading) {
      onSendMessage(inputValue);
      setInputValue('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  const toggleMessageDetails = (messageId) => {
    const newExpanded = new Set(expandedMessages);
    if (newExpanded.has(messageId)) {
      newExpanded.delete(messageId);
    } else {
      newExpanded.add(messageId);
    }
    setExpandedMessages(newExpanded);
  };

  const handleConfirmQuestion = async (confirmed) => {
    setIsConfirming(true);
    try {
      await onConfirmQuestion(confirmed);
    } finally {
      setIsConfirming(false);
    }
  };

  const handleRefineMessage = async (originalQuestion, feedback) => {
    setIsRefining(true);
    try {
      await onRefineMessage(originalQuestion, feedback);
    } finally {
      setIsRefining(false);
    }
  };

  return (
    <div className="chat-interface">
      {/* Header */}
      <div className="chat-header">
        <div className="header-content">
          <div className="logo">
            <div className="logo-icon">üîç</div>
            <span className="logo-text">SQL Assistant</span>
          </div>
        </div>
      </div>

      {/* Messages Area */}
      <div className="messages-container">
        <div className="messages">
          {messages.length === 0 && (
            <div className="welcome-message">
              <h2>Welcome to SQL Assistant</h2>
              <p>Ask me anything about your database in natural language!</p>
            </div>
          )}

          {messages.map((message) => (
            <div key={message.id} className={`message ${message.sender}`}>
              <div className="message-content">
                <div className="flex items-start justify-between">
                  <div className="message-text flex-1">{message.text}</div>
                  {message.sender === 'bot' && message.sqlQuery && (
                    <InfoIcon
                      onClick={() => toggleMessageDetails(message.id)}
                      isExpanded={expandedMessages.has(message.id)}
                    />
                  )}
                </div>

                {message.sender === 'bot' && message.sqlQuery && (
                  <MessageDetails
                    sqlQuery={message.sqlQuery}
                    rawData={message.rawData}
                    rowCount={message.rowCount}
                    isExpanded={expandedMessages.has(message.id)}
                  />
                )}

                {message.sender === 'bot' && message.needsRefinement && (
                  <RefinementSection
                    message={message}
                    onRefine={handleRefineMessage}
                    isRefining={isRefining}
                  />
                )}

                <div className="message-time">
                  {new Date(message.timestamp).toLocaleTimeString()}
                </div>
              </div>
            </div>
          ))}

          {isLoading && (
            <div className="message bot">
              <div className="message-content">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* Input Area */}
      <div className="input-container">
        <form onSubmit={handleSubmit} className="input-form">
          <input
            ref={inputRef}
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask us anything..."
            className="message-input"
            disabled={isLoading}
          />
          <button 
            type="submit" 
            className="send-button"
            disabled={!inputValue.trim() || isLoading}
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
              <path 
                d="M22 2L11 13M22 2L15 22L11 13M22 2L2 9L11 13" 
                stroke="currentColor" 
                strokeWidth="2" 
                strokeLinecap="round" 
                strokeLinejoin="round"
              />
            </svg>
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChatInterface;
